import rclpy 
from rclpy.node import Node
from sensor_msgs.msg import PointCloud2

class discriminator(Node):
    def __init__(self):
        super().__init__("discriminator")

        self.create_timer(1.0, self.lalal)
        self.create_subscription(PointCloud2, '/scan_points', self.points)

    def lalal(self):
        self.get_logger().info("Hi")

    def points(self):
        self.get_logger().warn('Fucker')




def main(args = None):
    rclpy.init(args=args)
    node = discriminator()
    rclpy.spin_once(node)
    node.destroy_node()
    rclpy.shutdown

if __name__ == '__main__':
    main()
    


